import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate'i ekliyoruz
import './Product.css';

const Product = () => {
    const [mainImage, setMainImage] = useState('/assets/product2.png');
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Giriş durumu için state ekliyoruz
    const navigate = useNavigate(); // navigate fonksiyonunu kullanmak için

    useEffect(() => {
        const userEmail = localStorage.getItem('userEmail'); // Giriş durumu kontrolü
        if (userEmail) {
            setIsLoggedIn(true);
        }
    }, []);

    const changeImage = (imageSrc) => {
        setMainImage(imageSrc);
    };

    const handleProfileClick = () => {
        navigate('/profile'); // Profil sayfasına yönlendirme
    };

    return (
        <div className="product-page">
            <header>
                <div className="logo" onClick={() => navigate('/')}>biletlik</div>
                <nav>
                    <ul>
                        {isLoggedIn ? (
                            <li><a href="/profile" className="profile-icon"><FaUserCircle size={24} /></a></li>
                        ) : (
                            <li><a href="/login" className="login-button">Giriş Yap</a></li>
                        )}
                    </ul>
                </nav>
            </header>
            <main>
                <div className="product-container">
                    <div className="product-image">
                        <img src={mainImage} alt="Ürün Resmi" />
                    </div>
                    <div className="product-details">
                        <h1>biletlik</h1>
                        <p className="product-description">Kişiye özel rfid bileklik</p>
                        <div className="color-options">
                            <p>Renk Seçenekleri:</p>
                            <button onClick={() => changeImage('/assets/product1.png')}></button>
                            <button onClick={() => changeImage('/assets/product2.png')}></button>
                            <button onClick={() => changeImage('/assets/product3.png')}></button>
                            <button onClick={() => changeImage('/assets/product4.png')}></button>
                        </div>
                        <div className="quantity">
                            <p>Adet:</p>
                            <input type="number" min="1" defaultValue="1" />
                        </div>
                        <div className="price">
                            <p>₺427.99</p>
                        </div>
                        <button className="payment-button">Sipariş Ver</button>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Product;
