import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';

const SignUp = () => {
    const [form, setForm] = useState({
        name: '',
        email: '',
        phone: '',
        password: ''
    });

    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({
            ...form,
            [name]: value
        });
    };

    // Ad Soyad Doğrulama
    const validateName = (name) => {
        const nameRegex = /^[a-zA-Z\s]+$/;
        return nameRegex.test(name);
    };

    // E-posta Doğrulama
    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    // Telefon Numarasını Düzenleme
    const formatPhoneNumber = (phone) => {
        // Telefon numarasındaki tüm boşlukları ve diğer karakterleri temizleyin
        const cleanedPhone = phone.replace(/\D/g, '');
        // Telefon numarasını formatlayın (başında "0" olacak şekilde)
        return `0${cleanedPhone}`;
    };

    // Şifre Doğrulama
    const validatePassword = (password) => {
        const passwordRegex = /^(?=.*[a-zA-Z])(?=.*\d)(?!.*\s).{6,}$/;
        return passwordRegex.test(password);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateName(form.name)) {
            setError('Ad Soyad sadece harf ve boşluk içermelidir.');
            return;
        }

        if (!validateEmail(form.email)) {
            setError('Geçerli bir e-posta adresi girin.');
            return;
        }

        const formattedPhone = formatPhoneNumber(form.phone);
        if (!validatePassword(form.password)) {
            setError('Şifre en az 6 karakter uzunluğunda olmalı, harf ve rakam içermeli ve boşluk olmamalıdır.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5001/api/signup', {
                ...form,
                phone: formattedPhone
            });
            setMessage('Kullanıcı başarıyla kaydedildi');
            setError('');
            setForm({
                name: '',
                email: '',
                phone: '',
                password: ''
            });
        } catch (error) {
            setError('Kayıt hatası: ' + (error.response?.data || 'Bilinmeyen hata'));
            setMessage('');
        }
    };

    return (
        <div className="signup-container">
            <div className="signup-header">
                <div className="logo">biletlik</div>
            </div>
            <div className="signup-form">
                <h2>Hesap Oluştur</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="name">Ad Soyad</label>
                        <input 
                            type="text" 
                            id="name" 
                            name="name" 
                            value={form.name} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="email">E-posta</label>
                        <input 
                            type="email" 
                            id="email" 
                            name="email" 
                            value={form.email} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="phone">Telefon Numarası</label>
                        <input 
                            type="tel" 
                            id="phone" 
                            name="phone" 
                            value={form.phone} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Şifre</label>
                        <input 
                            type="password" 
                            id="password" 
                            name="password" 
                            value={form.password} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <button type="submit" className="signup-button">Hesap Oluştur</button>
                </form>
                {message && <div className="message-success">{message}</div>}
                {error && <div className="message-error">{error}</div>}
                <div className="login-redirect">
                    <a href="/login">Zaten hesabınız var mı? Giriş Yapın</a>
                </div>
            </div>
        </div>
    );
};

export default SignUp;
