import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserCircle } from 'react-icons/fa'; // Profil ikonu için react-icons kütüphanesini kullanıyoruz
import './Product.css';

const Product = () => {
    const [mainImage, setMainImage] = useState('/assets/product2.png');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const userEmail = localStorage.getItem('userEmail');
        if (userEmail) {
            setIsLoggedIn(true);
        }
    }, []);

    const changeImage = (imageSrc) => {
        setMainImage(imageSrc);
    };

    const handleProfileClick = () => {
        navigate('/profile');
    };

    const handleLoginClick = () => {
        navigate('/login');
    };

    const handleOrderClick = () => {
        if (isLoggedIn) {
            navigate('/checkout'); // Ödeme ekranına yönlendir
        } else {
            alert('Ödeme yapabilmek için önce giriş yapmalısınız.');
            navigate('/login'); // Giriş sayfasına yönlendir
        }
    };

    return (
        <div className="product-page">
            <header>
                <div className="logo">biletlik</div>
                <nav>
                    <ul>
                        {isLoggedIn ? (
                            <li>
                                <button onClick={handleProfileClick} className="profile-button">
                                    <FaUserCircle size={24} />
                                </button>
                            </li>
                        ) : (
                            <li>
                                <button onClick={handleLoginClick} className="login-button">
                                    Giriş Yap
                                </button>
                            </li>
                        )}
                    </ul>
                </nav>
            </header>
            <main>
                <div className="product-container">
                    <div className="product-image">
                        <img src={mainImage} alt="Ürün Resmi" />
                    </div>
                    <div className="product-details">
                        <h1>biletlik</h1>
                        <p className="product-description">Kişiye özel rfid bileklik</p>
                        <div className="color-options">
                            <p>Renk Seçenekleri:</p>
                            <button onClick={() => changeImage('/assets/product1.png')}></button>
                            <button onClick={() => changeImage('/assets/product2.png')}></button>
                            <button onClick={() => changeImage('/assets/product3.png')}></button>
                            <button onClick={() => changeImage('/assets/product4.png')}></button>
                        </div>
                        <div className="quantity">
                            <p>Adet:</p>
                            <input type="number" min="1" defaultValue="1" />
                        </div>
                        <div className="price">
                            <p>₺427.99</p>
                        </div>
                        <button className="payment-button" onClick={handleOrderClick}>Sipariş Ver</button>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Product;
