import React, { useState } from 'react';
import './Cart.css';
import cartIcon from './shopping-bag-03.png'; // Sepet ikonu dosyasını buraya eklemeniz gerekiyor
import productImage from '/Users/erenozdemir/biletlik/public/assets/product1.png'; // Ürün resmini buraya eklemeniz gerekiyor

const Cart = () => {
    const [quantity, setQuantity] = useState(1);

    const handleQuantityChange = (change) => {
        setQuantity(prevQuantity => Math.max(1, prevQuantity + change));
    };

    const handleRemove = () => {
        // Sepeti boşaltma işlemi yapılabilir
    };

    return (
        <div className="cart-section">
            <div className="cart-images">
                <div className="main-image">
                    <img src={productImage} alt="Ürün Resmi" />
                </div>
            </div>
            <div className="cart-details">
                <h1>biletlik</h1>
                <div className="product-info">
                    <p className="description">Mavi</p>
                    <p className="quantity">
                        Adet: {quantity} 
                        <button className="dropdown-button" onClick={() => handleQuantityChange(1)}>▲</button>
                        <button className="dropdown-button" onClick={() => handleQuantityChange(-1)}>▼</button>
                    </p>
                    <p className="price">₺427.99</p>
                    <button className="remove-button" onClick={handleRemove}>&times;</button>
                </div>
                <button className="pay-button">Ödeme Yapın</button>
            </div>
        </div>
    );
};

export default Cart;
