import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Checkout.css';

const Checkout = () => {
    const [address, setAddress] = useState('');
    const [paymentInfo, setPaymentInfo] = useState('');
    const navigate = useNavigate();

    const handleAddressChange = (e) => {
        setAddress(e.target.value);
    };

    const handlePaymentInfoChange = (e) => {
        setPaymentInfo(e.target.value);
    };

    const handleConfirmOrder = () => {
        // Ödeme işlemi ve sipariş onayı işlemleri burada yapılabilir.
        // Şu an sadece görünüşte olacak.
        alert('Siparişiniz alınmıştır!');
        navigate('/'); // Ana sayfaya veya başka bir sayfaya yönlendirebilirsiniz.
    };

    return (
        <div className="checkout-page">
            <header>
                <div className="logo">biletlik</div>
                <nav>
                    <ul>
                        <li>
                            <button onClick={() => navigate('/profile')} className="profile-button">
                                Profil
                            </button>
                        </li>
                        <li>
                            <button onClick={() => navigate('/login')} className="login-button">
                                Giriş Yap
                            </button>
                        </li>
                    </ul>
                </nav>
            </header>
            <main>
                <div className="checkout-container">
                    <h1>Siparişinizi Tamamlayın</h1>
                    <div className="form-group">
                        <label htmlFor="address">Teslimat Adresi:</label>
                        <textarea
                            id="address"
                            value={address}
                            onChange={handleAddressChange}
                            placeholder="Teslimat adresinizi girin..."
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="payment-info">Ödeme Bilgileri:</label>
                        <input
                            type="text"
                            id="payment-info"
                            value={paymentInfo}
                            onChange={handlePaymentInfoChange}
                            placeholder="Ödeme bilgilerinizi girin..."
                        />
                    </div>
                    <button className="confirm-button" onClick={handleConfirmOrder}>Siparişi Onayla</button>
                </div>
            </main>
        </div>
    );
};

export default Checkout;
