import React, { useState } from 'react';
import './Login.css';

function Login() {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [setIsSubmitting] = useState(false); 
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [showOverlay, setShowOverlay] = useState(false); // Dolma ekranı durumu

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setShowOverlay(true); // Dolma ekranını göster

    // Fake API call simulation for demo purposes
    setTimeout(() => {
      setShowOverlay(false);
      setSubmissionMessage('Giriş başarılı, ana sayfaya yönlendiriliyorsunuz.');
    }, 3000);
  };

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  return (
    <div className="login-container">
      {showOverlay && (
        <div className="overlay">
          <div className="loader"></div>
          {submissionMessage && <div className="submission-message">{submissionMessage}</div>}
        </div>
      )}
      <div className="login-header">
        <div className="logo">biletlik</div>
      </div>
      <div className="login-form">
        <h2>Giriş Yap</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="email">E-posta</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-group password-group">
            <label htmlFor="password">Şifre</label>
            <div className="password-container">
              <input
                type={passwordVisible ? "text" : "password"}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <button type="button" className="toggle-password" onClick={togglePasswordVisibility}>
                <i className={`fa ${passwordVisible ? 'fa-eye-slash' : 'fa-eye'}`}></i>
              </button>
            </div>
          </div>
          <div className="login-options">
            <p className="forgot-password">Şifremi Unuttum</p>
          </div>
          <button type="submit" className="login-button">Giriş Yap</button>
        </form>
        <div className="create-account">
          <a href="/signup">Hesap Oluştur</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
