import React, { useEffect, useState } from 'react';
import './Homepage.css'; // Stil dosyasını import ediyoruz

function Homepage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Kullanıcı giriş yapmış mı kontrol et
    const loggedIn = localStorage.getItem('loggedIn') === 'true';
    setIsLoggedIn(loggedIn);
  }, []);

  return (
    <div className="homepage">
      <header>
        <div className="logo">biletlik</div>
        <nav>
          <ul>
            <li><a href="/about">Hakkımızda</a></li>
            <li><a href="/help">Yardım</a></li>
            {isLoggedIn ? (
              <li><a href="/profile" className="profile-button">Profil</a></li>
            ) : (
              <li><a href="/login" className="login-button">Giriş Yap</a></li>
            )}
          </ul>
        </nav>
      </header>
      <main>
        <div className="content">
          <h1>biletlik</h1>
          <p>Konserlerde seni öne geçirecek biletlik</p>
        </div>
        <a href="/product" className="buy-button">Satın Al</a>
      </main>
    </div>
  );
}

export default Homepage;
