import React, { useState } from 'react';
import './Checkout.css';

function Checkout() {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    postalCode: '',
    paymentMethod: 'creditCard'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Burada form verilerini işleme kodu olabilir, ancak bu örnekte sadece formu gösteriyoruz.
    alert('Siparişiniz alınmıştır!');
  };

  return (
    <div className="checkout-page">
      <header>
        <div className="logo">biletlik</div>
      </header>
      <main>
        <h1>Teslimat Bilgileriniz</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Adınız:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Adres:
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Şehir:
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Posta Kodu:
            <input
              type="text"
              name="postalCode"
              value={formData.postalCode}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Ödeme Yöntemi:
            <select
              name="paymentMethod"
              value={formData.paymentMethod}
              onChange={handleChange}
            >
              <option value="creditCard">Kredi Kartı</option>
              <option value="paypal">PayPal</option>
            </select>
          </label>
          <button type="submit" className="submit-button">Siparişi Tamamla</button>
        </form>
      </main>
    </div>
  );
}

export default Checkout;
