import React, { useState, useEffect } from 'react';
import './Profile.css';

function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://biletlik.net/backend/getUser.php');
        const data = await response.json();
        if (response.ok) {
          setUser(data.user);
        } else {
          setError(data.message);
        }
      } catch (error) {
        setError('Bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) {
    return <div>Yükleniyor...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="profile-container">
      <header>
        <div className="logo">
          <a href="/">biletlik</a>
        </div>
        <nav>
          <ul>
            <li><a href="/aboutus">Hakkımızda</a></li>
            <li><a href="/help">Yardım</a></li>
          </ul>
        </nav>
      </header>
      <main>
        <div className="profile-header">
          <h2>Merhaba, {user ? user.name : ''}</h2>
        </div>
        <div className="profile-content">
          <div className="profile-photo">
            <img src="/path/to/default-avatar.png" alt="Avatar" />
            <button>Fotoğraf Yükle</button>
          </div>
          <div className="profile-details">
            <div className="profile-info">
              <label>Ad</label>
              <input type="text" value={user ? user.name : ''} readOnly />
            </div>
            <div className="profile-info">
              <label>Soyad</label>
              <input type="text" value={user ? user.surname : ''} readOnly />
            </div>
            <div className="profile-info">
              <label>Doğum Tarihi</label>
              <input type="date" value={user ? user.birthdate : ''} readOnly />
            </div>
            <div className="profile-info">
              <label>Cinsiyet</label>
              <input type="text" value={user ? user.gender : ''} readOnly />
            </div>
            <h3>İletişim Bilgileri</h3>
            <div className="profile-info">
              <label>Telefon Numarası</label>
              <input type="text" value={user ? user.phone : ''} readOnly />
            </div>
            <div className="profile-info">
              <label>E-posta</label>
              <input type="email" value={user ? user.email : ''} readOnly />
            </div>
            <button className="save-button">Değişiklikleri Kaydet</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Profile;
