import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';

const SignUp = () => {
    const [form, setForm] = useState({
        name: '',
        email: '',
        phone: '',
        password: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({
            ...form,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Form verilerini backend'e gönder
            const response = await axios.post('http://yourdomain.com/auth/signup', {
                name: form.name,
                email: form.email,
                phone: form.phone,
                password: form.password,
            });
            console.log('Kayıt başarılı:', response.data);
        } catch (error) {
            console.error('Kayıt işlemi başarısız:', error);
        }
    };

    return (
        <div className="signup-container">
            <div className="signup-header">
                <div className="logo">biletlik</div>
            </div>
            <div className="signup-form">
                <h2>Hesap Oluştur</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="name">Ad Soyad</label>
                        <input 
                            type="text" 
                            id="name" 
                            name="name" 
                            value={form.name} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="email">E-posta</label>
                        <input 
                            type="email" 
                            id="email" 
                            name="email" 
                            value={form.email} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="phone">Telefon Numarası</label>
                        <input 
                            type="tel" 
                            id="phone" 
                            name="phone" 
                            value={form.phone} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Şifre</label>
                        <input 
                            type="password" 
                            id="password" 
                            name="password" 
                            value={form.password} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <button type="submit" className="signup-button">Hesap Oluştur</button>
                </form>
                <div className="login-redirect">
                    <a href="/login">Zaten hesabınız var mı? Giriş Yapın</a>
                </div>
            </div>
        </div>
    );
};

export default SignUp;
