import React, { useState } from 'react';
import './Login.css'; // Stil dosyasını import ediyoruz

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Başarılı giriş işlemi
        localStorage.setItem('loggedIn', 'true'); // Kullanıcının giriş yapıp yapmadığını kaydet
        window.location.href = '/'; // Ana sayfaya yönlendir
      } else {
        // Giriş başarısız, hata mesajını göster
        const errorData = await response.json();
        console.error('Giriş başarısız:', errorData.message);
        alert('Giriş başarısız: ' + errorData.message);
      }
    } catch (error) {
      console.error('Giriş yaparken bir hata oluştu:', error);
      alert('Giriş yaparken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-header">
        <div className="logo">biletlik</div>
      </div>
      <div className="login-form">
        <h2>Giriş Yap</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="email">E-posta</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Şifre</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="login-options">
            <p className="forgot-password">Şifremi Unuttum</p>
          </div>
          <button type="submit" className="login-button">Giriş Yap</button>
        </form>
        <div className="create-account">
          <a href="/signup">Hesap Oluştur</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
