import React, { useState } from 'react';
import './Checkout.css';

function Checkout({ selectedProduct }) {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    postalCode: '',
    paymentMethod: 'creditCard',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Siparişiniz alınmıştır!');
  };

  return (
    <div className="checkout-page">
      <header className="checkout-header">
        <div className="checkout-logo">biletlik</div>
      </header>
      <main>
        <div className="checkout-container">
          <div className="checkout-product-summary">
            <img src={selectedProduct.image} alt="Ürün Resmi" />
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.price}</p>
          </div>
          <div className="checkout-form-container">
            <h1>Teslimat Bilgileriniz</h1>
            <form onSubmit={handleSubmit}>
              <label>
                Adınız:
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Adres:
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Şehir:
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Posta Kodu:
                <input
                  type="text"
                  name="postalCode"
                  value={formData.postalCode}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Ödeme Yöntemi:
                <select
                  name="paymentMethod"
                  value={formData.paymentMethod}
                  onChange={handleChange}
                >
                  <option value="creditCard">Kredi Kartı</option>
                  <option value="paypal">PayPal</option>
                </select>
              </label>
              <button type="submit" className="checkout-submit-button">
                Siparişi Tamamla
              </button>
            </form>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Checkout;
