import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // navigate için import
import './Login.css'; 

function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const [isSubmitting, setIsSubmitting] = useState(false); // Form gönderimi durumu
    const [submissionMessage, setSubmissionMessage] = useState(''); // Gönderim mesajı
    const [showOverlay, setShowOverlay] = useState(false); // Dolma ekranı durumu
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        setIsSubmitting(true);
        setShowOverlay(true); // Dolma ekranını göster

        try {
            const response = await fetch('http://biletlik.net/backend/login.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();

            setTimeout(() => {
                if (response.ok && data.message === "Giriş başarılı") {
                    setSubmissionMessage('Giriş başarılı. Ana ekrana yönlendiriliyorsunuz.');
                    setTimeout(() => {
                        navigate('/'); // Ana sayfaya yönlendirme
                    }, 2000);
                } else {
                    setSubmissionMessage(data.message);
                }
                setShowOverlay(false); // Dolma ekranını gizle
            }, 3000); // Dolma ekranının 3 saniye görünmesini sağlar

        } catch (error) {
            console.error('Giriş hatası:', error);
            setSubmissionMessage('Bir hata oluştu.');
            setShowOverlay(false); // Dolma ekranını gizle
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="login-container">
            {showOverlay && (
                <div className="overlay">
                    <div className="loader"></div>
                </div>
            )}
            <div className="login-header">
                <div className="logo">biletlik</div>
            </div>
            <div className="login-form">
                <h2>Giriş Yap</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="email">E-posta</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group password-group">
                        <label htmlFor="password">Şifre</label>
                        <div className="password-container">
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                            />
                            <button type="button" className="toggle-password">
                                <i className="fa fa-eye"></i>
                            </button>
                        </div>
                    </div>
                    <div className="login-options">
                        <p className="forgot-password">Şifremi Unuttum</p>
                    </div>
                    <button type="submit" className="login-button">
                        {isSubmitting ? (
                            <span className="spinner"></span>
                        ) : (
                            'Giriş Yap'
                        )}
                    </button>
                </form>
                {submissionMessage && <p className="submission-message">{submissionMessage}</p>}
                <div className="create-account">
                    <a href="/signup">Hesap Oluştur</a>
                </div>
            </div>
        </div>
    );
}

export default Login;
