import React, { useEffect, useState } from 'react';
import './Homepage.css';

function Homepage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    setIsLoggedIn(false);
    // İsteğe bağlı olarak logout işlemi sonrası sayfayı yenileyebilirsiniz
    // window.location.reload();
  };

  return (
    <div className="homepage">
      <header>
        <div className="logo">biletlik</div>
        <nav>
          <ul>
            <li><a href="/aboutus">Hakkımızda</a></li>
            <li><a href="/help">Yardım</a></li>
            {isLoggedIn ? (
              <>
                <li><a href="/profile">Profil</a></li>
                <li><button onClick={handleLogout}>Çıkış Yap</button></li>
              </>
            ) : (
              <li><a href="/login" className="login-button">Giriş Yap</a></li>
            )}
          </ul>
        </nav>
      </header>
      <main>
        <div className="content">
          <h1>biletlik</h1>
          <p>konserlerde seni öne geçirecek biletlik</p>
        </div>
        <a href="/product" className="buy-button">Satın Al</a>
      </main>
    </div>
  );
}

export default Homepage;
