import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate import edilmesi gerekebilir
import './Profile.css';

function Profile() {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate(); // navigate fonksiyonunu tanımlıyoruz

  useEffect(() => {
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      fetchUserData(userEmail);
    }
  }, []);

  const fetchUserData = async (email) => {
    try {
      const response = await fetch(`http://biletlik.net/backend/profile.php?email=${email}`);
      const data = await response.json();
      setUserData(data);
    } catch (error) {
      console.error('Kullanıcı verisi çekilirken hata oluştu:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    navigate('/'); // Ana sayfaya yönlendirme
  };

  return (
    <div className="profile-container">
      <header>
        <div className="logo" onClick={() => navigate('/')}>biletlik</div>
        <button onClick={handleLogout} className="logout-button">Çıkış Yap</button>
      </header>
      <main>
        {userData ? (
          <>
            <h1>Profil Bilgileri</h1>
            <p><strong>İsim:</strong> {userData.name}</p>
            <p><strong>E-posta:</strong> {userData.email}</p>
            <p><strong>Telefon:</strong> {userData.phone}</p>
          </>
        ) : (
          <p>Yükleniyor...</p>
        )}
      </main>
    </div>
  );
}

export default Profile;
