// src/pages/Product.js
import React, { useState } from 'react';
import './Product.css'; // CSS dosyanızı buraya import edin

const Product = () => {
    const [mainImage, setMainImage] = useState('/Users/erenozdemir/biletlik/public/assets/product2.png');

    const changeImage = (imageSrc) => {
        setMainImage(imageSrc);
    };

    return (
        <div className="product-container">
            <div className="product-content">
                <div className="product-image">
                    <img id="mainImage" src={mainImage} alt="Ürün Resmi" />
                </div>
                <div className="product-info">
                    <h1>biletlik</h1>
                    <div className="color-options">
                        <p>Renk:</p>
                        <img src="/Users/erenozdemir/biletlik/public/assets/product1.png" alt="Renk 1" onClick={() => changeImage('/Users/erenozdemir/biletlik/public/assets/product2.png')} />
                        <img src="/Users/erenozdemir/biletlik/public/assets/product2.png" alt="Renk 2" onClick={() => changeImage('/Users/erenozdemir/biletlik/public/assets/product2.png')} />
                        <img src="/Users/erenozdemir/biletlik/public/assets/product3.png" alt="Renk 3" onClick={() => changeImage('/Users/erenozdemir/biletlik/public/assets/product2.png')} />
                        <img src="/Users/erenozdemir/biletlik/public/assets/product4.png" alt="Renk 4" onClick={() => changeImage('/Users/erenozdemir/biletlik/public/assets/product2.png')} />
                    </div>
                    <div className="quantity">
                        <label htmlFor="quantity">Adet:</label>
                        <input type="number" id="quantity" name="quantity" min="1" defaultValue="1" />
                    </div>
                    <p className="price">₺427.99</p>
                    <button className="buy-button">Ödeme Yap</button>
                </div>
            </div>
        </div>
    );
};

export default Product;
